package com.class07;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int time=8;
		if (time<12) {
			System.out.println("Good Morning (IF Ststment)"); //code execuate one time
		}
	
	while(time<12) {  //checking the condition if true ,will execuate 
	System.out.println("good morning (While Ststment)");
	time++;  //Increment
	}
	}

}
