package com.class7;

public class WhileAddEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=50;
int b=100;

while(a<=100) {
	if(a%2==1) {
		System.out.println(a+" ");
	}
a++;
	}
System.out.println("----------------------");
while(b<=100) {
	if(b%2==0) {
		System.out.println(b+" ");
	}
b--;
	}
	}
}
